CREATE DATABASE explorer;

CREATE TABLE content(
    id SERIAL PRIMARY KEY,
    name VARCHAR NOT NULL,
    medium VARCHAR,
    author VARCHAR,
    type VARCHAR NOT NULL,
    genre VARCHAR,
    description VARCHAR NOT NULL,
    imageURL VARCHAR NOT NULL,
    whereToFind VARCHAR NOT NULL
);


CREATE TABLE users(
    user_id uuid PRIMARY KEY DEFAULT
    uuid_generate_v4(),
    user_name VARCHAR(255) NOT NULL,
    user_email VARCHAR(255) NOT NULL,
    user_password VARCHAR(255) NOT NULL
);

-- ALTER users unique user_name

CREATE TABLE list(
    list_id SERIAL PRIMARY KEY,
    u_id uuid,
    c_id INT,
    FOREIGN KEY (u_id) REFERENCES users (user_id),
    FOREIGN KEY (c_id) REFERENCES content (id)
);

-- create voting system(posts)
CREATE TYPE scorechange AS ENUM ('upvote', 'downvote');

CREATE TABLE votes(
    u_id uuid,
    c_id INT,
    values scorechange NOT NULL,
    PRIMARY KEY(u_id, c_id),
    FOREIGN KEY (u_id) REFERENCES users (user_id),
    FOREIGN KEY (c_id) REFERENCES content (id)

);

create TABLE comments(
    comment_id SERIAL PRIMARY KEY,
    u_id uuid NOT NULL,
    user_name VARCHAR(255) NOT NULL,
    c_id INT NOT NULL, 
    votes INT, 
    parent_comment_id INT, 
    child_comment_id INT,
    posting_date TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (u_id) REFERENCES users (user_id),
    FOREIGN KEY (user_name) REFERENCES users (user_name),
    FOREIGN KEY (c_id) REFERENCES content (id),
    comment_text TEXT
);











